import React, { useEffect, useState } from 'react';
import offersData from './offers.json';
import OfferCard from './components/OfferCard';

// inside return
{filteredOffers.map((offer, index) => (
    <OfferCard key={index} offer={offer} />
))}


function App() {
    const [offers, setOffers] = useState([]);
    const [search, setSearch] = useState('');

    useEffect(() => {
        // In real scenario you fetch from backend
        setOffers(offersData);
    }, []);

    const filteredOffers = offers.filter(offer =>
        offer.store.toLowerCase().includes(search.toLowerCase())
    );

    return (
        <div className="container my-5">
            <header className="text-center mb-5">
                <h1>ðŸ”¥ Consolidated Offers Hub</h1>
                <p className="lead">All the best cashback & deals in one place!</p>
            </header>

            <div className="mb-4">
                <input
                    type="text"
                    className="form-control"
                    placeholder="Search by store..."
                    value={search}
                    onChange={(e) => setSearch(e.target.value)}
                />
            </div>

            <div className="row">
                {filteredOffers.length === 0 && (
                    <p className="text-center">No offers found.</p>
                )}
                {filteredOffers.map((offer, index) => (
                    <div key={index} className="col-md-4 mb-4">
                        <div className="card h-100 shadow-sm">
                            <div className="card-body">
                                <h5 className="card-title">{offer.store}</h5>
                                <p className="card-text">
                                    Cashback: <strong>{offer.cashback}</strong>
                                </p>
                                <p className="card-text text-muted">
                                    Scraped on: {offer.scrapedOn || 'N/A'}
                                </p>
                            </div>
                        </div>
                    </div>
                ))}
            </div>

            <footer className="text-center mt-5 mb-3 text-muted">
                Â© 2025 Consolidated Offers Hub. All rights reserved.
            </footer>
        </div>
    );
}

export default App;
