import React, { useEffect, useState } from 'react';
import offersData from './offers.json';
import OfferCard from './components/OfferCard';

function App() {
    const [offers, setOffers] = useState([]);
    const [search, setSearch] = useState('');

    useEffect(() => {
        setOffers(offersData);
    }, []);

    const filteredOffers = offers.filter(offer =>
        offer.store.toLowerCase().includes(search.toLowerCase())
    );

    return (
        <div className="row">
            {filteredOffers.length === 0 && (
                <p className="text-center">No offers found.</p>
            )}
            {filteredOffers.map((offer, index) => (
                <div
                    key={index}
                    className="col-12 col-sm-6 col-md-4 mb-3"
                >
                    <div
                        className="card h-100 shadow-sm p-3 offer-card"
                        style={{
                            transition: "transform 0.2s, box-shadow 0.2s",
                        }}
                    >
                        <div className="card-body">
                            <h5 className="card-title">{offer.store}</h5>
                            <p className="card-text">
                                Cashback:{" "}
                                <span
                                    className="badge text-white"
                                    style={{
                                        backgroundColor:
                                            parseFloat(offer.cashback) >= 20
                                                ? "green"
                                                : parseFloat(offer.cashback) >= 10
                                                    ? "blue"
                                                    : "goldenrod",
                                        fontSize: "0.85rem",
                                        padding: "0.25rem 0.5rem",
                                    }}
                                >
                            {offer.cashback}%
                        </span>
                            </p>
                            <p className="card-text text-muted">
                                Scraped on: {offer.scraped_at || "N/A"}
                            </p>
                            {offer.link && (
                                <a
                                    href={offer.link}
                                    target="_blank"
                                    rel="noopener noreferrer"
                                    className="btn btn-sm btn-outline-primary mt-2"
                                >
                                    Visit Store
                                </a>
                            )}
                        </div>
                    </div>
                </div>
            ))}
        </div>

    );
}

export default App;
